import React, { useState, useEffect } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import './ChessBoard.css';

const ChessBoard = ({ onGameEnd }) => {
  const [game, setGame] = useState(() => new Chess());
  const [boardOrientation, setBoardOrientation] = useState('white');
  const [isAIThinking, setIsAIThinking] = useState(false);
  const [moveHistory, setMoveHistory] = useState([]);
  const [playerSide, setPlayerSide] = useState('white'); // Player plays as white by default

  // Effect to make AI move if it's AI's turn at the start
  useEffect(() => {
    // If game just started and player is black, let AI make first move
    if (game.history().length === 0 && playerSide === 'black') {
      makeAIMove();
    }
  }, [playerSide]);

  // Function to make a move
  function makeMove(move) {
    const gameCopy = new Chess(game.fen());
    try {
      const result = gameCopy.move(move);
      setGame(gameCopy);

      // Update move history
      if (result) {
        setMoveHistory([...moveHistory, result.san]);
      }

      return result; // null if the move was invalid
    } catch (error) {
      console.error('Invalid move:', error);
      return null;
    }
  }

  // Handle piece drop from user
  function onDrop(sourceSquare, targetSquare) {
    // Check if it's player's turn
    const isWhiteTurn = game.turn() === 'w';
    if ((playerSide === 'white' && !isWhiteTurn) || (playerSide === 'black' && isWhiteTurn)) {
      console.log("Not your turn!");
      return false;
    }

    // Make user move
    const move = makeMove({
      from: sourceSquare,
      to: targetSquare,
      promotion: 'q', // always promote to a queen for simplicity
    });

    // If invalid move
    if (move === null) return false;

    // Check if game is over
    if (game.isGameOver()) {
      handleGameOver();
      return true;
    }

    // AI makes a move
    setTimeout(makeAIMove, 300);
    return true;
  }

  // Make AI move
  async function makeAIMove() {
    setIsAIThinking(true);

    try {
      console.log("Requesting AI move for position:", game.fen());
      // Call your FastAPI backend
      const response = await fetch('http://localhost:8000/chess/get-move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fen: game.fen() }),
      });

      if (!response.ok) {
        throw new Error(`Server responded with status: ${response.status}`);
      }

      const data = await response.json();
      console.log("Received AI move data:", data);

      // Make the AI move
      try {
        const moveResult = makeMove({
          from: data.move.from,
          to: data.move.to,
          promotion: data.move.promotion || 'q'
        });
        console.log("AI move result:", moveResult);

        // Check if game is over after AI move
        if (game.isGameOver()) {
          handleGameOver();
        }
      } catch (moveError) {
        console.error("Error applying AI move:", moveError);
        console.error("Move data:", data.move);
        console.error("Current game state:", game.fen());
      }
    } catch (error) {
      console.error('Error getting AI move:', error);
    } finally {
      setIsAIThinking(false);
    }
  }

  // Handle when game is over
  function handleGameOver() {
    let result = 'draw';
    if (game.isCheckmate()) {
      result = game.turn() === 'w' ? 'black' : 'white';
    }

    // Save game data
    saveGameData(result);

    // Notify parent component
    if (onGameEnd) {
      onGameEnd({
        result,
        pgn: game.pgn(),
        history: moveHistory,
      });
    }
  }

  // Save game data to backend
  async function saveGameData(result) {
    try {
      await fetch('http://localhost:8000/chess/save-game', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pgn: game.pgn(),
          result: result,
        }),
      });
    } catch (error) {
      console.error('Error saving game:', error);
    }
  }

  // Reset the game
  function resetGame() {
    setGame(new Chess());
    setMoveHistory([]);

    // If player is black, AI should make first move
    if (playerSide === 'black') {
      setTimeout(makeAIMove, 300);
    }
  }

  // Toggle player side
  function switchSides() {
    const newSide = playerSide === 'white' ? 'black' : 'white';
    setPlayerSide(newSide);
    setBoardOrientation(newSide);

    // If switching mid-game and it's AI's turn, make AI move
    const isWhiteTurn = game.turn() === 'w';
    if ((newSide === 'black' && isWhiteTurn) || (newSide === 'white' && !isWhiteTurn)) {
      setTimeout(makeAIMove, 300);
    }
  }

  return (
    <div className="chessboard-container">
      <div className="board">
        <Chessboard 
          position={game.fen()} 
          onPieceDrop={onDrop} 
          boardOrientation={boardOrientation}
          customBoardStyle={{
            borderRadius: '5px',
            boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)'
          }}
        />
      </div>

      <div className="controls">
        <button onClick={resetGame}>New Game</button>
        <button onClick={switchSides}>Play as {playerSide === 'white' ? 'Black' : 'White'}</button>
      </div>

      <div className="status">
        {isAIThinking && <div>AI is thinking...</div>}
        <div>You are playing as: {playerSide}</div>
        <div>Current turn: {game.turn() === 'w' ? 'White' : 'Black'}</div>
      </div>

      {game.isGameOver() && (
        <div className="game-over">
          {game.isCheckmate() 
            ? `Checkmate! ${game.turn() === 'w' ? 'Black' : 'White'} wins!` 
            : 'Game ended in a draw'}
        </div>
      )}
    </div>
  );
};

export default ChessBoard;
