import React, { useState, useEffect, useCallback } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import './ChessBoard.css';

const ChessBoard = ({ onGameEnd }) => {
  const [game, setGame] = useState(() => new Chess());
  const [boardOrientation, setBoardOrientation] = useState('white');
  const [isAIThinking, setIsAIThinking] = useState(false);
  const [moveHistory, setMoveHistory] = useState([]);
  const [playerColor, setPlayerColor] = useState('white'); // Player plays as white by default
  const [selectedSquare, setSelectedSquare] = useState(null);
  const [legalMoveSquares, setLegalMoveSquares] = useState({});

  // Debug function to show all legal moves from a position
  function showLegalMoves(sourceSquare) {
    if (!sourceSquare) return [];

    // Get all legal moves for the piece at the source square
    const legalMoves = game.moves({
      square: sourceSquare,
      verbose: true
    });

    console.log(`Legal moves from ${sourceSquare}:`, legalMoves);
    return legalMoves;
  }

  // Make AI move - wrapped in useCallback to fix dependency warning
  const makeAIMove = useCallback(async () => {
    // Prevent multiple simultaneous AI moves
    if (isAIThinking) {
      console.log("AI is already thinking, skipping move request");
      return;
    }

    setIsAIThinking(true);
    console.log("AI is thinking...");

    try {
      // IMPORTANT FIX: Create a fresh copy of the current game state
      const currentGame = new Chess(game.fen());
      const currentFEN = currentGame.fen();

      console.log("Current FEN for AI move:", currentFEN);

      // Call your FastAPI backend
      const response = await fetch('http://localhost:8000/chess/get-move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fen: currentFEN }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server responded with ${response.status}: ${errorText}`);
      }

      const data = await response.json();
      console.log("AI selected move:", data.move);

      // Validate the move data
      if (!data.move || !data.move.from || !data.move.to) {
        throw new Error("Invalid move data received from AI: " + JSON.stringify(data));
      }

      // Create a new game instance with the current state
      const gameCopy = new Chess(currentFEN);

      try {
        // Apply the AI move using the exact move object
        const moveObj = {
          from: data.move.from,
          to: data.move.to,
          promotion: data.move.promotion
        };

        console.log("AI move being applied:", moveObj);
        const result = gameCopy.move(moveObj);

        if (!result) {
          throw new Error("Failed to apply AI move: " + JSON.stringify(moveObj));
        }

        console.log("AI move successfully applied:", result);
        console.log("New board position:", gameCopy.fen());

        // Update the game state with the successful move
        setGame(gameCopy);

        // Update move history
        setMoveHistory(prev => [...prev, result.san]);

        // Check if game is over after AI move
        if (gameCopy.isGameOver()) {
          handleGameOver();
        }
      } catch (moveError) {
        console.error("Error applying AI move:", moveError);

        // Fallback: Try to find a legal move that matches as closely as possible
        const legalMoves = gameCopy.moves({ verbose: true });
        console.log("Available legal moves:", legalMoves);

        const fallbackMove = legalMoves.find(move => 
          move.from === data.move.from && move.to === data.move.to
        );

        if (fallbackMove) {
          console.log("Found fallback legal move:", fallbackMove);
          const result = gameCopy.move(fallbackMove);

          // Update state with fallback move
          setGame(gameCopy);
          setMoveHistory(prev => [...prev, result.san]);

          if (gameCopy.isGameOver()) {
            handleGameOver();
          }
        } else {
          console.error("No matching legal move found for AI's intended move");
        }
      }
    } catch (error) {
      console.error("Error during AI move:", error);
    } finally {
      setIsAIThinking(false);
    }
  }, [game, isAIThinking]);

  function makePlayerMove(sourceSquare, targetSquare) {
    console.log("Attempting player move from", sourceSquare, "to", targetSquare);

    // Create a new game state from the current position
    const gameCopy = new Chess(game.fen());

    // Get all legal moves for this specific piece and square
    const legalMoves = gameCopy.moves({
      square: sourceSquare,
      verbose: true
    });

    // Find the EXACT matching move for the target square
    const legalMove = legalMoves.find(move => move.to === targetSquare);
    if (!legalMove) {
      console.error(`${targetSquare} is not a legal target from ${sourceSquare}`);
      return false;
    }

    try {
      // FIXED: Use the exact move object from the legal moves list instead of constructing a new one
      console.log("Move being applied:", legalMove);

      // Apply the move using the san (Standard Algebraic Notation) or the move object directly
      const result = gameCopy.move(legalMove.san) || gameCopy.move({
        from: legalMove.from,
        to: legalMove.to,
        promotion: legalMove.promotion
      });

      if (!result) {
        console.error("Failed to apply legal move:", legalMove);
        return false;
      }

      console.log("Player move successfully applied:", result);
      console.log("New board position:", gameCopy.fen());

      // Update the game state with the successful move
      setGame(gameCopy);

      // Update move history
      setMoveHistory(prev => [...prev, result.san]);

      // Check if game is over
      if (gameCopy.isGameOver()) {
        handleGameOver();
        return true;
      }

      // AI makes a move after a short delay
      setTimeout(makeAIMove, 300);
      return true;
    } catch (error) {
      console.error("Error making player move:", error);
      return false;
    }
  }

  // Handle piece drop from user
  function onDrop(sourceSquare, targetSquare) {
    // Only allow moves if it's the player's turn
    const currentTurn = game.turn() === 'w' ? 'white' : 'black';
    if (currentTurn !== playerColor) {
      console.log("Not your turn!");
      return false;
    }

    console.log("User attempting move from", sourceSquare, "to", targetSquare);

    // Get all legal moves from the source square 
    const legalMoves = showLegalMoves(sourceSquare);

    // Check if the target square is a legal destination
    const isLegalTarget = legalMoves.some(move => move.to === targetSquare);

    if (!isLegalTarget) {
      console.error(`${targetSquare} is not a legal target from ${sourceSquare}`);
      return false;
    }

    // FIX: Use the simplified move function that preserves the exact move
    return makePlayerMove(sourceSquare, targetSquare);
  }

  // Check if it's AI's turn when the component mounts or game state changes
  useEffect(() => {
    // If it's AI's turn and the game is not over and AI is not already thinking
    if (!isAIThinking) {
      const timeoutId = setTimeout(() => {
        // Get the current turn from the latest game state
        const currentGame = new Chess(game.fen());
        const currentTurn = currentGame.turn() === 'w' ? 'white' : 'black';

        if (currentTurn !== playerColor && !currentGame.isGameOver()) {
          makeAIMove();
        }
      }, 500);

      // Cleanup timeout if component unmounts or dependencies change
      return () => clearTimeout(timeoutId);
    }
  }, [game, playerColor, isAIThinking, makeAIMove]);

  // Handle when game is over
  function handleGameOver() {
    let result = 'draw';
    if (game.isCheckmate()) {
      result = game.turn() === 'w' ? 'black' : 'white';
    }

    console.log("Game over! Result:", result);

    // Save game data
    saveGameData(result);

    // Notify parent component
    if (onGameEnd) {
      onGameEnd({
        result,
        pgn: game.pgn(),
        history: moveHistory,
      });
    }
  }

  // Save game data to backend
  async function saveGameData(result) {
    try {
      await fetch('http://localhost:8000/chess/save-game', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pgn: game.pgn(),
          result: result,
        }),
      });
    } catch (error) {
      console.error('Error saving game:', error);
    }
  }

  // Reset the game
  function resetGame() {
    setGame(new Chess());
    setMoveHistory([]);
    setSelectedSquare(null);
    setLegalMoveSquares({});

    // If player is black, AI (white) should make the first move
    if (playerColor === 'black') {
      setTimeout(makeAIMove, 500);
    }
  }

  // Toggle board orientation and player color
  function flipBoard() {
    const newOrientation = boardOrientation === 'white' ? 'black' : 'white';
    setBoardOrientation(newOrientation);
    setPlayerColor(newOrientation);

    // Reset the game when switching sides
    const newGame = new Chess();
    setGame(newGame);
    setMoveHistory([]);
    setSelectedSquare(null);
    setLegalMoveSquares({});

    // If player is now black, AI (white) should make the first move
    if (newOrientation === 'black') {
      setTimeout(makeAIMove, 500);
    }
  }

  // Handle square click for click-to-move functionality
  function onSquareClick(square) {
    console.log("Square clicked:", square);

    // Only allow interaction when it's the player's turn
    const currentTurn = game.turn() === 'w' ? 'white' : 'black';
    if (currentTurn !== playerColor) {
      console.log("Not your turn!");
      return;
    }

    // If no square is selected yet, select this square if it has a piece of the player's color
    if (!selectedSquare) {
      const piece = game.get(square);
      if (piece) {
        const pieceColor = piece.color === 'w' ? 'white' : 'black';

        // Only allow selecting pieces of the player's color
        if (pieceColor === playerColor) {
          setSelectedSquare(square);

          // Get and show legal moves for the selected piece
          const legalMoves = showLegalMoves(square);

          // Create a map of legal target squares for highlighting
          const legalSquares = {};
          legalMoves.forEach(move => {
            legalSquares[move.to] = {
              background: 'radial-gradient(circle, rgba(0,0,0,.1) 25%, transparent 25%)',
              borderRadius: '50%'
            };
          });

          // Set the legal move squares for highlighting
          setLegalMoveSquares(legalSquares);
        }
      }
      return;
    }

    // If a square is already selected, try to move from the selected square to this square
    if (selectedSquare) {
      // If clicking the same square, deselect it
      if (selectedSquare === square) {
        setSelectedSquare(null);
        setLegalMoveSquares({});
        return;
      }

      // Check if this is a legal move
      const legalMoves = showLegalMoves(selectedSquare);
      const isLegalTarget = legalMoves.some(move => move.to === square);

      if (!isLegalTarget) {
        // If the new square has a piece of the player's color, select it instead
        const piece = game.get(square);
        if (piece) {
          const pieceColor = piece.color === 'w' ? 'white' : 'black';
          if (pieceColor === playerColor) {
            setSelectedSquare(square);

            // Update legal move highlights for the new selected piece
            const newLegalMoves = showLegalMoves(square);
            const newLegalSquares = {};
            newLegalMoves.forEach(move => {
              newLegalSquares[move.to] = {
                background: 'radial-gradient(circle, rgba(0,0,0,.1) 25%, transparent 25%)',
                borderRadius: '50%'
              };
            });

            setLegalMoveSquares(newLegalSquares);
            return;
          }
        }

        // Otherwise, just clear the selection
        setSelectedSquare(null);
        setLegalMoveSquares({});
        return false;
      }

      // FIX: Use the simplified move function that preserves the exact move
      const result = makePlayerMove(selectedSquare, square);

      // Clear selection and legal move highlights
      setSelectedSquare(null);
      setLegalMoveSquares({});

      return result;
    }
  }

  // Function to determine if a piece is draggable
  function isDraggablePiece({ piece, sourceSquare }) {
    // Only allow dragging pieces of the player's color
    const pieceColor = piece.charAt(0).toLowerCase() === 'w' ? 'white' : 'black';
    const currentTurn = game.turn() === 'w' ? 'white' : 'black';

    // Only allow dragging if it's the player's turn and the piece is of the player's color
    return currentTurn === playerColor && pieceColor === playerColor;
  }

  // Get custom square styles to highlight the selected square and legal moves
  function getCustomSquareStyles() {
    const combinedStyles = { ...legalMoveSquares };

    // Add highlight for the selected square
    if (selectedSquare) {
      combinedStyles[selectedSquare] = {
        backgroundColor: 'rgba(255, 255, 0, 0.4)',
      };
    }

    return combinedStyles;
  }


  return (
    <div className="chessboard-container">
      <div className="board">
        <Chessboard 
          position={game.fen()} 
          onPieceDrop={onDrop} 
          boardOrientation={boardOrientation}
          customBoardStyle={{
            borderRadius: '5px',
            boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)'
          }}
          arePiecesDraggable={true}
          animationDuration={200}
          isDraggablePiece={isDraggablePiece}
          onSquareClick={onSquareClick}
          customSquareStyles={getCustomSquareStyles()}
        />
      </div>

      <div className="controls">
        <button onClick={resetGame}>New Game</button>
        <button onClick={flipBoard}>Play as {boardOrientation === 'white' ? 'Black' : 'White'}</button>
      </div>

      <div className="game-info">
        <div className="player-info">
          Playing as: <strong>{playerColor}</strong>
        </div>
        {isAIThinking && <div className="status">AI is thinking...</div>}

        {game.isGameOver() && (
          <div className="game-over">
            {game.isCheckmate() 
              ? `Checkmate! ${game.turn() === 'w' ? 'Black' : 'White'} wins!` 
              : 'Game ended in a draw'}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChessBoard;
