import React, { useState, useEffect } from 'react';
import { Chessboard } from 'react-chessboard';
import { Chess } from 'chess.js';
import './ChessBoard.css';

const ChessBoard = ({ onGameEnd }) => {
  const [game, setGame] = useState(() => new Chess());
  const [boardOrientation, setBoardOrientation] = useState('white');
  const [isAIThinking, setIsAIThinking] = useState(false);
  const [moveHistory, setMoveHistory] = useState([]);
  const [playerColor, setPlayerColor] = useState('white'); // Player plays as white by default

  // Handle piece drop from user
  function onDrop(sourceSquare, targetSquare) {
    // Only allow moves if it's the player's turn
    const currentTurn = game.turn() === 'w' ? 'white' : 'black';
    if (currentTurn !== playerColor) {
      console.log("Not your turn!");
      return false;
    }

    console.log("User attempting move from", sourceSquare, "to", targetSquare);

    // Get the piece that's being moved
    const piece = game.get(sourceSquare);
    if (!piece) {
      console.error("No piece found at source square:", sourceSquare);
      return false;
    }

    // Check if this is a pawn promotion move
    const isPromotion = piece.type === 'p' && 
                       ((targetSquare.charAt(1) === '8' && piece.color === 'w') || 
                        (targetSquare.charAt(1) === '1' && piece.color === 'b'));

    // Create the move object
    const moveObj = {
      from: sourceSquare,
      to: targetSquare,
    };

    // Only add promotion if it's actually a promotion move
    if (isPromotion) {
      moveObj.promotion = 'q'; // promote to queen
      console.log("This is a promotion move");
    }

    // Try to make the move directly
    const gameCopy = new Chess(game.fen());
    try {
      const result = gameCopy.move(moveObj);

      if (!result) {
        console.error("Invalid move:", moveObj);
        return false;
      }

      // Update the game state with the successful move
      setGame(gameCopy);

      // Update move history
      setMoveHistory([...moveHistory, result.san]);

      console.log("User move applied:", result);

      // Check if game is over
      if (gameCopy.isGameOver()) {
        handleGameOver();
        return true;
      }

      // AI makes a move after a short delay
      setTimeout(makeAIMove, 300);
      return true;
    } catch (error) {
      console.error("Error making move:", error);
      return false;
    }
  }

  // Make AI move
  async function makeAIMove() {
    setIsAIThinking(true);
    console.log("AI is thinking...");
    console.log("Current FEN:", game.fen());

    try {
      // Call your FastAPI backend
      const response = await fetch('http://localhost:8000/chess/get-move', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ fen: game.fen() }),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Server responded with ${response.status}: ${errorText}`);
      }

      const data = await response.json();
      console.log("AI selected move:", data.move);

      // Validate the move data
      if (!data.move || !data.move.from || !data.move.to) {
        throw new Error("Invalid move data received from AI: " + JSON.stringify(data));
      }

      // Create the move object
      const moveObj = {
        from: data.move.from,
        to: data.move.to,
      };

      // Only include promotion if it's specified and not null
      if (data.move.promotion) {
        moveObj.promotion = data.move.promotion;
        console.log("AI move includes promotion to:", data.move.promotion);
      }

      // Try to make the move directly
      const gameCopy = new Chess(game.fen());
      const result = gameCopy.move(moveObj);

      if (!result) {
        console.error("AI move is invalid:", moveObj);

        // Try to find a legal move as fallback
        const legalMoves = game.moves({ verbose: true });
        if (legalMoves.length > 0) {
          const randomMove = legalMoves[Math.floor(Math.random() * legalMoves.length)];
          console.log("Making random move instead:", randomMove);

          const fallbackResult = gameCopy.move(randomMove);
          if (fallbackResult) {
            setGame(gameCopy);
            setMoveHistory([...moveHistory, fallbackResult.san]);
          }
        }
        return;
      }

      // Update the game state with the successful move
      setGame(gameCopy);

      // Update move history
      setMoveHistory([...moveHistory, result.san]);

      console.log("AI move applied:", result);

      // Check if game is over after AI move
      if (gameCopy.isGameOver()) {
        handleGameOver();
      }
    } catch (error) {
      console.error('Error getting AI move:', error);
      // If there's an error with the AI move, we might want to handle it gracefully
      // For example, we could make a random legal move instead
      try {
        const legalMoves = game.moves({ verbose: true });
        if (legalMoves.length > 0) {
          const randomMove = legalMoves[Math.floor(Math.random() * legalMoves.length)];
          console.log("Making random move instead:", randomMove);

          const gameCopy = new Chess(game.fen());
          const result = gameCopy.move(randomMove);
          if (result) {
            setGame(gameCopy);
            setMoveHistory([...moveHistory, result.san]);
          }
        }
      } catch (fallbackError) {
        console.error("Failed to make fallback move:", fallbackError);
      }
    } finally {
      setIsAIThinking(false);
    }
  }

  // Handle when game is over
  function handleGameOver() {
    let result = 'draw';
    if (game.isCheckmate()) {
      result = game.turn() === 'w' ? 'black' : 'white';
    }

    console.log("Game over! Result:", result);

    // Save game data
    saveGameData(result);

    // Notify parent component
    if (onGameEnd) {
      onGameEnd({
        result,
        pgn: game.pgn(),
        history: moveHistory,
      });
    }
  }

  // Save game data to backend
  async function saveGameData(result) {
    try {
      await fetch('http://localhost:8000/chess/save-game', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          pgn: game.pgn(),
          result: result,
        }),
      });
    } catch (error) {
      console.error('Error saving game:', error);
    }
  }

  // Reset the game
  function resetGame() {
    setGame(new Chess());
    setMoveHistory([]);

    // If player is black, AI (white) should make the first move
    if (playerColor === 'black') {
      setTimeout(makeAIMove, 500);
    }
  }

  // Toggle board orientation and player color
  function flipBoard() {
    const newOrientation = boardOrientation === 'white' ? 'black' : 'white';
    setBoardOrientation(newOrientation);
    setPlayerColor(newOrientation);

    // Reset the game when switching sides
    const newGame = new Chess();
    setGame(newGame);
    setMoveHistory([]);

    // If player is now black, AI (white) should make the first move
    if (newOrientation === 'black') {
      setTimeout(makeAIMove, 500);
    }
  }

  // Check if it's AI's turn when the component mounts or game state changes
  useEffect(() => {
    const currentTurn = game.turn() === 'w' ? 'white' : 'black';

    // If it's AI's turn and the game is not over
    if (currentTurn !== playerColor && !game.isGameOver() && !isAIThinking) {
      setTimeout(makeAIMove, 300);
    }
  }, [game, playerColor, isAIThinking]);

  return (
    <div className="chessboard-container">
      <div className="board">
        <Chessboard 
          position={game.fen()} 
          onPieceDrop={onDrop} 
          boardOrientation={boardOrientation}
          customBoardStyle={{
            borderRadius: '5px',
            boxShadow: '0 5px 15px rgba(0, 0, 0, 0.5)'
          }}
        />
      </div>

      <div className="controls">
        <button onClick={resetGame}>New Game</button>
        <button onClick={flipBoard}>Play as {boardOrientation === 'white' ? 'Black' : 'White'}</button>
      </div>

      <div className="game-info">
        <div className="player-info">
          Playing as: <strong>{playerColor}</strong>
        </div>
        {isAIThinking && <div className="status">AI is thinking...</div>}

        {game.isGameOver() && (
          <div className="game-over">
            {game.isCheckmate() 
              ? `Checkmate! ${game.turn() === 'w' ? 'Black' : 'White'} wins!` 
              : 'Game ended in a draw'}
          </div>
        )}
      </div>
    </div>
  );
};

export default ChessBoard;
